<script setup lang="ts">
import {getAssetsFile,getRootFile} from "../utils/getImg";
import anime from 'animejs';
import {onMounted, ref, nextTick} from "vue";
import { showToast } from 'vant';
import { useIntervalFn } from '@vueuse/core'

// count will increase every 200ms
// const {pause,resume} = useIntervalFn(Time,1000)

const warn = ref(false)
const isCanClick = ref(false)
const audio = ref(null)


const ourTime = ref({})
console.log(ourTime)
const emit =  defineEmits(['next'])
const animationInit = anime.timeline({
  autoplay: false, complete: () => {
    isCanClick.value = true
  }
})
onMounted(() => {
  animationInit.add({
    targets: '.book-bg',
    opacity: '1',
    duration: 500,
    easing: 'easeInOutSine'
  })
      .add({
        targets: '.book-left',
        left: '0px',
        bottom: '0px',
        duration: 800,
        easing: 'easeInOutSine'
      }, 0)
      .add({
        targets: '.book-right',
        right: '0px',
        bottom: '0px',
        duration: 800,
        easing: 'easeInOutSine'
      }, 0)
      .add({
        targets: '.book-center',
        opacity: 1,
        bottom: '0px',
        duration: 800,
        easing: 'easeInOutSine'
      }, 0)
      .add({
        targets: '.book-yuan',
        bottom: '0px',
        top: '30px',
        left: '76px',
        duration: 1000,
        easing: 'easeInOutQuad'
      })
      .add({
        targets: '.book-moon',
        top: '30px',
        opacity: 1,
        duration: 1000,
        easing: 'easeInOutQuad'
      }, 0)
      .add({
        targets: '.book-text',
        left: '28px',
        duration: 500,
        easing: 'easeOutBounce'
      })
  animationInit.play()
})
const clickAnimation = anime.timeline({autoplay: false})

function openBook(type: string) {
  if (!isCanClick.value) {
    showToast('别着急，先看完哈哈');
    return
  }
  (audio.value as any).play()
  const otherBook = type == 'book1' ? 'book1' : 'book2'
  if (type === 'book1') {
    warn.value = true


    showToast('这边暂时保密，不能看！！');
    return;
  }
  (audio.value as any).onpause = ()=>{
    emit('next','1')
  }

}
</script>
